mapper.select:
select
from table_blood_purify where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}
limit #{start}, #{size}

select count(id) from table_blood_purify where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}

SELECT IFNULL(max(CASE WHEN code='beginTime' THEN content END),"") AS beginTime, IFNULL(max(CASE WHEN code='endTime' THEN content END),"") AS endTime, maintenance_id from table_blood_purify WHERE 1=1
and status= #{status}
GROUP BY maintenance_id limit #{start}, #{size}

SELECT count(maintenance_id) from table_blood_purify WHERE 1=1
and status= #{status}
GROUP BY maintenance_id

select
from table_blood_purify where 1=1 and status != -1
and maintenance_id =#{maintenanceId}

SELECT visit_id, visit_code, patient_id, maintenance_id, IFNULL(max(CASE WHEN code='beginTime' THEN content END),"") AS beginTime, IFNULL(max(CASE WHEN code='endTime' THEN content END),"") AS endTime, IFNULL(max(CASE WHEN code='punctureLeft' THEN content END),"") AS punctureLeft, IFNULL(max(CASE WHEN code='punctureRight' THEN content END),"") AS punctureRight, IFNULL(max(CASE WHEN code='thighVein' THEN content END),"") AS thighVein, IFNULL(max(CASE WHEN code='neckVein' THEN content END),"") AS neckVein, IFNULL(max(CASE WHEN code='clavicleVein' THEN content END),"") AS clavicleVein, IFNULL(max(CASE WHEN code='innerVein' THEN content END),"") AS innerVein, IFNULL(max(CASE WHEN code='duration' THEN content END),"") AS duration, IFNULL(max(CASE WHEN code='preWashLiquid' THEN content END),"") AS preWashLiquid, IFNULL(max(CASE WHEN code='heparin' THEN content END),"") AS heparin, IFNULL(max(CASE WHEN code='lowMolecularHeparin' THEN content END),"") AS lowMolecularHeparin, IFNULL(max(CASE WHEN code='agatroban' THEN content END),"") AS agatroban, IFNULL(max(CASE WHEN code='citrate' THEN content END),"") AS citrate, IFNULL(max(CASE WHEN code='sealLiquid' THEN content END),"") AS sealLiquid, IFNULL(max(CASE WHEN code='sealTime' THEN content END),"") AS sealTime, IFNULL(max(CASE WHEN code='cvvh' THEN content END),"") AS cvvh, IFNULL(max(CASE WHEN code='cvvhd' THEN content END),"") AS cvvhd, IFNULL(max(CASE WHEN code='cvvhdf' THEN content END),"") AS cvvhdf, IFNULL(max(CASE WHEN code='pe' THEN content END),"") AS pe, IFNULL(max(CASE WHEN code='hp' THEN content END),"") AS hp, IFNULL(max(CASE WHEN code='beginTreater' THEN content END),"") AS beginTreater, IFNULL(max(CASE WHEN code='checker' THEN content END),"") AS checker, IFNULL(max(CASE WHEN code='endTreater' THEN content END),"") AS endTreater, IFNULL(max(CASE WHEN code='doctorSignature' THEN content END),"") AS doctorSignature, IFNULL(max(CASE WHEN code='dataTime' THEN content END),"") AS dataTime from table_blood_purify WHERE 1=1
and status= #{status}
and maintenance_id =#{maintenanceId}
GROUP BY maintenance_id
