mapper.select:
select
from table_handover where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}
limit #{start}, #{size}

select count(id) from table_handover where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}

SELECT IFNULL(th.id,"") AS id, IFNULL(th.visit_id,"") AS visit_id, IFNULL(th.visit_code,"") AS visit_code, IFNULL(th.patient_id,"") AS patient_id, IFNULL(th.info_date,"") AS info_date, th.shift, IFNULL(th.handover_time,"") AS handover_time, IFNULL(th.handover_begin_time,"") AS handover_begin_time, IFNULL(th.handover_end_time,"") AS handover_end_time, IFNULL(tsa.user_name,"") AS send_operator, IFNULL(tsb.user_name,"") AS receive_operator, IFNULL(th.send_description,"") AS send_description, IFNULL(th.receive_description,"") AS receive_description, th.status FROM table_handover th LEFT JOIN table_user tsa ON tsa.login_name=th.send_operator LEFT JOIN table_user tsb ON tsb.login_name=th.receive_operator WHERE 1=1 and th.status !=-1
and th.patient_id =#{patientId}
and th.send_operator =#{sendOperator}
and th.receive_operator =#{receiveOperator}
and th.status= #{status}
and #{beginTime} >= th.handover_begin_time
and #{endTime} <= th.handover_end_time
limit #{start}, #{size}

select
count(id)
from
table_handover
where
1=1
and status !=-1

    and patient_id =#{patientId}


    and  send_operator =#{sendOperator}


    and  receive_operator =#{receiveOperator}


    and  status= #{status}


    and  #{beginTime}  >=  handover_begin_time


    and  #{endTime}  <=  handover_end_time
