mapper.select:
select
from table_vital_sign where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}
limit #{start}, #{size}

select count(id) from table_vital_sign where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}

SELECT

FROM
    table_vital_sign
WHERE
    1 = 1
AND status !=- 1

    AND code = #{code}


    AND patient_id = #{patientId}


    AND date_format(
    data_time,
    '%Y-%m-%d %H:%i:%s'
    ) = date_format(
    #{dataTime},
    '%Y-%m-%d %H:%i:%s'
    )


    and  status= #{status}

select
from table_vital_sign where 1=1 and status !=-1
AND patient_id = #{patientId}
AND date_format( data_time, '%Y-%m-%d %H:%i:%s' ) = date_format( #{dataTime}, '%Y-%m-%d %H:%i:%s' )
and status= #{status}

SELECT
    IFNULL(max(CASE WHEN code='airWayStyle' THEN content END),"")  AS airWayStyle
FROM
    table_vital_sign
WHERE
    1 = 1
AND status !=-1
AND status = #{status}
AND patient_id = #{patientId}
AND date_format(data_time, '%Y-%m-%d %H:%i') BETWEEN #{startTime} AND #{endTime}
