mapper.select:
select
from table_surgery_nursing where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}
limit #{start}, #{size}

select count(id) from table_surgery_nursing where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}

SELECT ts.user_name, tnp.maintenance_id, IFNULL(max(CASE WHEN tnp.code='operationDate' THEN tnp.content END),"") AS 'operationDate', IFNULL(max(CASE WHEN tnp.code='operationName' THEN tnp.content END),"") AS 'operationName' FROM table_surgery_nursing tnp LEFT JOIN table_user ts ON ts.login_name = tnp.operator_code WHERE 1=1 AND tnp.status!=-1
and tnp.patient_id =#{patientId}
and tnp.status= #{status}
GROUP BY tnp.maintenance_id limit #{start}, #{size}

SELECT count(tnp.maintenance_id) FROM table_surgery_nursing tnp LEFT JOIN table_user ts ON ts.login_name = tnp.operator_code WHERE 1=1 AND tnp.status!=-1
and tnp.patient_id =#{patientId}
and tnp.status= #{status}
GROUP BY tnp.maintenance_id

SELECT IFNULL(tnp.visit_id,"") AS visit_id, IFNULL(tnp.visit_code,"") AS visit_code, IFNULL(tnp.patient_id,"") AS patient_id, IFNULL(tnp.maintenance_id,"") AS maintenance_id, IFNULL(max(CASE WHEN tnp.code='visitDate' THEN tnp.content END),"") AS 'visitDate', IFNULL(max(CASE WHEN tnp.code='diagnosis' THEN tnp.content END),"") AS 'diagnosis', IFNULL(max(CASE WHEN tnp.code='operationDate' THEN tnp.content END),"") AS 'operationDate', IFNULL(max(CASE WHEN tnp.code='operationName' THEN tnp.content END),"") AS 'operationName', IFNULL(max(CASE WHEN tnp.code='temperature' THEN tnp.content END),"") AS 'temperature', IFNULL(max(CASE WHEN tnp.code='pulse' THEN tnp.content END),"") AS 'pulse', IFNULL(max(CASE WHEN tnp.code='hr' THEN tnp.content END),"") AS 'hr', IFNULL(max(CASE WHEN tnp.code='br' THEN tnp.content END),"") AS 'br', IFNULL(max(CASE WHEN tnp.code='bp' THEN tnp.content END),"") AS 'bp', IFNULL(max(CASE WHEN tnp.code='presurgeryTeach' THEN tnp.content END),"") AS 'presurgeryTeach', IFNULL(max(CASE WHEN tnp.code='preparedSkin' and tnp.content='1' THEN '1' WHEN tnp.code='preparedSkin' and tnp.content='2' THEN '2' END),"") AS 'preparedSkin', IFNULL(max(CASE WHEN tnp.code='bladderEmpty' and tnp.content='1' THEN '1' WHEN tnp.code='bladderEmpty' and tnp.content='2' THEN '2' END),"") AS 'bladderEmpty', IFNULL(max(CASE WHEN tnp.code='inspectionAndAssaySheetCompleted' and tnp.content='1' THEN '1' WHEN tnp.code='inspectionAndAssaySheetCompleted' and tnp.content='2' THEN '2' END),"") AS 'inspectionAndAssaySheetCompleted', IFNULL(max(CASE WHEN tnp.code='mentalStatus' and tnp.content='1' THEN '1' WHEN tnp.code='mentalStatus' and tnp.content='2' THEN '2' WHEN tnp.code='mentalStatus' and tnp.content='3' THEN '3' END),"") AS 'mentalStatus', IFNULL(max(CASE WHEN tnp.code='venousAccessSite' THEN tnp.content END),"") AS 'venousAccessSite', IFNULL(max(CASE WHEN tnp.code='antibioticSkinTest' THEN tnp.content END),"") AS 'antibioticSkinTest', IFNULL(max(CASE WHEN tnp.code='leaveTime' THEN tnp.content END),"") AS 'leaveTime', IFNULL(max(CASE WHEN tnp.code='leaveNurseSignature' THEN ts0.user_name END),"") AS 'leaveNurseSignature', IFNULL(max(CASE WHEN tnp.code='backTime' THEN tnp.content END),"") AS 'backTime', IFNULL(max(CASE WHEN tnp.code='backNurseSignature' THEN ts1.user_name END),"") AS 'backNurseSignature', IFNULL(max(CASE WHEN tnp.code='repressionDuration' THEN tnp.content END),"") AS 'repressionDuration', IFNULL(max(CASE WHEN tnp.code='repressBeginTime' THEN tnp.content END),"") AS 'repressBeginTime', IFNULL(max(CASE WHEN tnp.code='repressEndTime' THEN tnp.content END),"") AS 'repressEndTime' FROM table_nursing_plan tnp LEFT JOIN table_user ts0 ON ts0.login_name = tnp.content AND tnp.code='leaveNurseSignature' LEFT JOIN table_user ts1 ON ts1.login_name = tnp.content AND tnp.code='backNurseSignature' WHERE 1=1 AND tnp.status!=-1
AND tnp.maintenance_id =#{maintenanceId}
AND tnp.status=#{status}
GROUP BY tnp.maintenance_id
