mapper.select:
select
from table_surgery_nursing_maintain where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}
limit #{start}, #{size}

select count(id) from table_surgery_nursing_maintain where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}

select

from
table_surgery_nursing_maintain
where
1=1
and status != -1

    and  status= #{status}


    and  patient_id =#{patientId}


    and  maintenance_id =#{maintenanceId}


    and  code =#{code}


    and  data_time =#{dataTime}

select IFNULL(tsnm.patient_id,"") AS patient_id, IFNULL(tsnm.maintenance_id,"") AS maintenance_id, IFNULL(tsnm.maintenance_record_id,"") AS maintenance_record_id, date_format(tsnm.data_time, '%Y-%m-%d %H:%i') AS dataTime, IFNULL(max(CASE WHEN tsnm.code='temperature' THEN tsnm.content END),"") AS 'temperature', IFNULL(max(CASE WHEN tsnm.code='hr' THEN tsnm.content END),"") AS 'hr', IFNULL(max(CASE WHEN tsnm.code='br' THEN tsnm.content END),"") AS 'br', IFNULL(max(CASE WHEN tsnm.code='bp' THEN tsnm.content END),"") AS 'bp', IFNULL(max(CASE WHEN tsnm.code='spo2' THEN tsnm.content END),"") AS 'spo2', IFNULL(max(CASE WHEN tsnm.code='bleed' THEN tsnm.content END),"") AS 'bleed', IFNULL(max(CASE WHEN tsnm.code='tipCirculation' THEN tsnm.content END),"") AS 'tipCirculation', IFNULL(max(CASE WHEN tsnm.code='veinCatheterName' THEN dci.common_item_name END),"") AS 'veinCatheterName', IFNULL(max(CASE WHEN tsnm.code='veinCatheterNursing' THEN tsnm.content END),"") AS 'veinCatheterNursing', IFNULL(max(CASE WHEN tsnm.code='specialMedication' THEN tsnm.content END),"") AS 'specialMedication', IFNULL(max(CASE WHEN tsnm.code='nursingMeasure' THEN tsnm.content END),"") AS 'nursingMeasure', IFNULL(max(CASE WHEN tsnm.code='signature' THEN ts.user_name END),"") AS 'signature' from table_surgery_nursing_maintain tsnm left join dict_common_item dci ON dci.common_item_code=tsnm.content AND dci.type='vein_catheter' left join table_user ts on ts.login_name=tsnm.content where 1=1 and tsnm.status != -1
and tsnm.patient_id =#{patientId}
and date_format(tsnm.data_time, '%Y-%m-%d %H:%i')=date_format(#{dataTime}, '%Y-%m-%d %H:%i')
and tsnm.status= #{status}
group by tsnm.maintenance_record_id
