mapper.select:
select
from table_transfer_assessment where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}
limit #{start}, #{size}

select count(id) from table_transfer_assessment where 1=1 and status != -1
and content like '%${content}%'
and status= #{status}

select

from
table_transfer_assessment
where
1=1
and  status != -1

    and  assessment_id =#{assessmentId}


    and  patient_id =#{patientId}


    and  status= #{status}

select

from
table_transfer_assessment
where
1=1
and  status != -1

    and  assessment_id =#{assessmentId}


    and date_format(data_time, '%Y-%m-%d %H:%i:%s')=#{dataTime}


    and  status= #{status}

SELECT tra.data_time AS data_time, tra.assessment_id AS assessment_id, tu.user_name AS user_name FROM table_transfer_assessment tra LEFT JOIN table_user tu ON tu.login_name = tra.operator_code WHERE 1 = 1 and tra.status != -1
and tra.status= #{status}
and tra.patient_id=#{patientId}
GROUP BY tra..assessment_id ORDER BY data_time limit #{start}, #{size}

SELECT count(assessment_id) FROM table_transfer_assessment WHERE 1 = 1 and status != -1
and status= #{status}
and patient_id=#{patientId}
GROUP BY tha.assessment_id

SELECT IFNULL(max(tra.visit_id),"") AS visit_id, IFNULL(max(tra.visit_code),"") AS visit_code, IFNULL(max(tra.patient_id),"") AS patient_id, IFNULL(max(tra.assessment_id),"") AS assessment_id, IFNULL(max(CASE WHEN tra.code='sourceDepartment' THEN tra.content END),"") AS sourceDepartment, IFNULL(max(CASE WHEN tra.code='bedName' THEN tra.content END),"") AS bedName, IFNULL(max(CASE WHEN tra.code='name' THEN tra.content END),"") AS name, IFNULL(max(CASE WHEN tra.code='sex' THEN tra.content END),"") AS sex, IFNULL(max(CASE WHEN tra.code='diagnosisName' THEN tra.content END),"") AS diagnosisName, IFNULL(max(CASE WHEN tra.code='targetDepartment' THEN tra.content END),"") AS targetDepartment, IFNULL(max(CASE WHEN tra.code='wristTape' THEN tra.content END),"") AS wristTape, IFNULL(max(CASE WHEN tra.code='syncTime' THEN tra.content END),"") AS syncTime, IFNULL(max(CASE WHEN tra.code='t' THEN tra.content END),"") AS t, IFNULL(max(CASE WHEN tra.code='p' THEN tra.content END),"") AS p, IFNULL(max(CASE WHEN tra.code='r' THEN tra.content END),"") AS r, IFNULL(max(CASE WHEN tra.code='bp' THEN tra.content END),"") AS bp, IFNULL(max(CASE WHEN tra.code='spo2' THEN tra.content END),"") AS 'spo2', IFNULL(max(CASE WHEN tra.code='consciousness' THEN tra.content END),"") AS consciousness, IFNULL(max(CASE WHEN tra.code='otherConsciousness' THEN tra.content END),"") AS otherConsciousness, IFNULL(max(CASE WHEN tra.code='infusionPunctureType' THEN tra.content END),"") AS infusionPunctureType, IFNULL(max(CASE WHEN tra.code='infustionClear' THEN tra.content END),"") AS infustionClear, IFNULL(max(CASE WHEN tra.code='isInfustioning' THEN tra.content END),"") AS isInfustioning, IFNULL(max(CASE WHEN tra.code='infustionPathNum' THEN tra.content END),"") AS infustionPathNum, IFNULL(max(CASE WHEN tra.code='infustionDrug' THEN tra.content END),"") AS infustionDrug, IFNULL(max(CASE WHEN tra.code='infustionLeftOver' THEN tra.content END),"") AS infustionLeftOver, IFNULL(max(CASE WHEN tra.code='skinComplete' THEN tra.content END),"") AS skinComplete, IFNULL(max(CASE WHEN tra.code='skinOtherDescription' THEN tra.content END),"") AS skinOtherDescription, IFNULL(max(CASE WHEN tra.code='hasArtificialAirway' THEN tra.content END),"") AS hasArtificialAirway, IFNULL(max(CASE WHEN tra.code='artificialAirwayType' THEN tra.content END),"") AS artificialAirwayType, IFNULL(max(CASE WHEN tra.code='hasDrainageTube' THEN tra.content END),"") AS hasDrainageTube, IFNULL(max(CASE WHEN tra.code='drainageTubeList' THEN tra.content END),"") AS drainageTubeList, IFNULL(max(CASE WHEN tra.code='otherDrainageTube' THEN tra.content END),"") AS otherDrainageTube, IFNULL(max(CASE WHEN tra.code='hasMedicalRecord' THEN tra.content END),"") AS hasMedicalRecord, IFNULL(max(CASE WHEN tra.code='hasSpecialThing' THEN tra.content END),"") AS hasSpecialThing, IFNULL(max(CASE WHEN tra.code='specialThing' THEN tra.content END),"") AS specialThing, IFNULL(max(CASE WHEN tra.code='specialTransfer' THEN tra.content END),"") AS specialTransfer, IFNULL(max(CASE WHEN tra.code='transferNurseSignature' THEN tua.user_name END),"") AS transferNurseSignature, IFNULL(max(CASE WHEN tra.code='transferTime' THEN tra.content END),"") AS transferTime, IFNULL(max(CASE WHEN tra.code='inNurseSignature' THEN tub.user_name END),"") AS inNurseSignature, IFNULL(max(CASE WHEN tra.code='inTime' THEN tra.content END),"") AS inTime FROM table_transfer_assessment tra LEFT JOIN table_user tua ON tua.login_name=tra.content LEFT JOIN table_user tub ON tub.login_name=tra.content WHERE 1 = 1 and tra.status != -1
and tra.patient_id=#{patientId}
and tra.assessment_id=#{assessmentId}
and tra.status= #{status}
GROUP BY tra.assessment_id
